functions.function:
text = text.replace(/</g, '&lt;').replace(/>/g, '&gt;');

delete args.type;
delete args.page;
delete args.module;
delete args.menu;

var options = '';
for (var arg in args) {
    options += arg + ": " + args[arg] + "; ";
}

miniLOL.modules.list.SyntaxHighlighter.options = args;

text = '<code class="#{options}">#{source}</code><script>miniLOL.module.execute("SyntaxHighlighter", miniLOL.modules.list.SyntaxHighlighter.options); delete miniLOL.modules.list.SyntaxHighlighter.options</script>'.interpolate({
    options: options,
    source:  text,
});
